mapper.update:
update orders
number=#{number}, status=#{status}, address_book_id=#{addressBookId}, order_time=#{orderTime}, checkout_time=#{checkoutTime}, pay_method=#{payMethod}, pay_status=#{payStatus}, amount=#{amount}, remark=#{remark}, phone=#{phone}, address=#{address}, user_name=#{userName}, consignee=#{consignee} , cancel_reason=#{cancelReason}, rejection_reason=#{rejectionReason}, cancel_time=#{cancelTime}, estimated_delivery_time=#{estimatedDeliveryTime}, delivery_status=#{deliveryStatus}, delivery_Time=#{deliveryTime}, pack_amount=#{packAmount}, tableware_number=#{tablewareNumber}, tableware_status=#{tablewareStatus},
where id=#{id}

mapper.select:
        select * from orders


                and number = ('%',#{number},'%')


                and phone = ('%',#{phone},'%')


                and status = #{status}


                and order_time between #{beginTime} and #{endTime}


--                  这是>=是 >= 的意思，下面同理
                and order_time >= #{beginTime}


                and order_time <= #{endTime}


        order by order_time desc

select sum(status) from orders


    order_time between #{begin} and #{end}


    and status = #{status}

select count(*) from orders


        order_time between #{begin} and #{end}


        and status = #{status}

select count(*) from orders


        order_time between #{begin} and #{end}


        and status = #{status}
